package com.techcanvass.day1;

public class myDoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count= 1;
		int num = 5;
		do {
			
			System.out.println(num +" * " +count +" = " +num*count);
			count ++;
		} while (count<=10);
	}

}
